INSERT INTO programs (name, is_major, requirement, url) VALUES ('Music, B.A.', '1', '[{"name": "A. Complete the following:", "child": [{"name": "", "child": ["MUSIC 15A", "MUSIC 16A", "MUSIC 16B", "MUSIC 16C", "MUSIC 16D", "MUSIC 40B", "MUSIC 40C", "MUSIC 40D"]}, {"name": "*Exception: MUSIC 40B, MUSIC 40C, MUSIC 40D must be completed with a grade of D or higher.", "child": []}, {"name": "MUSIC 15 is a repeatable course students must take starting at the beginning of their major studies. At the end of the course, there is a Musicianship Proficiency Exam, which students must pass within six quarters of entering the major.", "child": []}]}, {"name": "B. Select six quarters of instrumental or vocal instruction for Music majors from the following:", "child": [{"name": "(b) MUSIC 65-70", "child": ["MUSIC 65", "MUSIC 66", "MUSIC 67", "MUSIC 68", "MUSIC 69", "MUSIC 70"]}]}, {"name": "Additional Requirements for the Bachelor of Arts Degree", "child": [{"name": "In addition to completing the common curriculum, students in the B.A. program must pass the departmental piano proficiency examination or take three quarters of MUSIC 10 (Piano for Majors) with a grade of C or better and complete the following course requirements:", "child": []}, {"name": "(b) A. Select three courses in Theory, Composition, and Technology from the following:", "child": ["MUSIC 51", "MUSIC 131", "MUSIC 132", "MUSIC 136", "MUSIC 147", "MUSIC 150", "MUSIC 151", "MUSIC 152", "MUSIC 153", "MUSIC 155", "MUSIC 157", "MUSIC 183A", "MUSIC 183B", "MUSIC 183C", "MUSIC 189"]}, {"name": "(b) B. Select three courses in Music and Culture (including at least two upper-division courses) from the following:", "child": ["AFAM 143", "AFAM 144", "MUSIC 4", "MUSIC 8", "MUSIC 9", "MUSIC 41", "MUSIC 42", "MUSIC 44", "MUSIC 45", "MUSIC 48", "MUSIC 78", "MUSIC 82A", "MUSIC 82B", "MUSIC 82C", "MUSIC 140", "MUSIC 141", "MUSIC 142", "MUSIC 143", "MUSIC 144", "MUSIC 145", "MUSIC 146", "MUSIC 148", "MUSIC 149", "MUSIC 156A", "MUSIC