INSERT INTO major_reqs (name, major_requirement, required_courses, url) VALUES ('Physics, B.S.', '[{"name": "Departmental Requirements", "child": [{"name": "", "child": [["MATH 2A", "MATH 5A"], "MATH 2B", "MATH 2D", "MATH 2E", "MATH 3A", "MATH 3D", "PHYSICS 7C", "PHYSICS 7D", "PHYSICS 7E", "PHYSICS 7LC", "PHYSICS 7LD", "PHYSICS 50", "PHYSICS 52A", "PHYSICS 52B", "PHYSICS 52C", "PHYSICS 53", "PHYSICS 60", "PHYSICS 61A", ["PHYSICS 61B", "PHYSICS 61C"], "PHYSICS 111A", "PHYSICS 111B", "PHYSICS 112A", "PHYSICS 112B", "PHYSICS 113A", "PHYSICS 115A", "PHYSICS 121W", "PHYSICS 125A", "PHYSICS 194"]}, {"name": "And select five additional coherently related four-unit courses. (This requirement is normally satisfied by concentrations, specializations, and tracks, as listed below.)", "child": []}]}, {"name": "Upper-Division Writing Requirement:", "child": [{"name": "Physics majors are required to satisfy the upper-division writing requirement by completing PHYSICS 194 with a grade of C or better, followed by PHYSICS 121W with a grade of C or better.", "child": ["PHYSICS 194", "PHYSICS 121W"]}]}, {"name": "Concentration in Computational Physics", "child": [{"name": "The Computational Physics concentration provides training for positions in software development in a wide variety of high-technology fields. For example, consider medical imaging software for magnetic resonance imaging. To write a first-rate program, one must understand the apparatus and analysis techniques (physics), use appropriate numerical techniques (numerical analysis), and employ a convenient object-oriented interface (computer science). The concentration develops this unique set of skills: physical and mathematical insight through the Physics curriculum, knowledge of modern computer programming techniques, and knowledge of numerical analysis.", "child": []}, {"name": "(b) Three courses in computer science:", "child": ["I&C SCI 31", "I&C SCI 32", "I&C SCI 33"]}, {"name": "(b) Two courses in numerical analysis plus the accompanying laboratories:", "child": ["MATH 105A", "MATH 105B", "MATH 105LA", "MATH 105LB"]}, {"name": "(b) One advanced computational course and accompanying laboratory:", "child": ["MATH 107", "MATH 107L"]}]}, {"name": "Concentration in Philosophy of Physics", "child": [{"name": "The Philosophy of Physics concentration is concerned with the study of the conceptual history of physics, the method of inquiry that has led to our best physical theories, and the structure and interpretation of the theories themselves. Students take courses in deductive and inductive logic, the philosophy and history of physics, and quantum mechanics. The emphasis on careful argument makes this concentration useful for anyone who wishes to pursue a graduate degree in philosophy or law, or for other careers that employ both verbal and quantitative analysis.", "child": []}, {"name": "(b) Select one from the following:", "child": ["LPS 40", "LPS 60", "SOC SCI H1G", "LPS H125", "LPS H80"]}, {"name": "or another approved Campuswide Honors course", "child": []}, {"name": "(b) Select one of the following:", "child": ["LPS 30", "LPS 104", "LPS 105A", "LPS 105B", "LPS 105C", "MATH 150"]}, {"name": "(b) Complete:", "child": ["LPS 31"]}, {"name": "(b) Select one from the following:", "child": ["LPS 104", "LPS 105A", "LPS 105B", "LPS 105C", "LPS 140", "MATH 150"]}, {"name": "(b) Complete", "child": ["PHYSICS 113B"]}, {"name": "(b) Select three from the following:", "child": ["LPS 141B", "LPS 141D"]}, {"name": "or other approved Philosophy of Physics course", "child": []}]}, {"name": "Concentration in Physics Education", "child": [{"name": "The Physics Education concentration is for students who plan a career in secondary education. An Education course, four general science courses, a research methods course, and two quarters of classroom experience complete the requirements for the concentration. Students are encouraged to take PHYSICS 191 (outreach).", "child": []}, {"name": "(b) Requirements", "child": ["EDUC 55", "PHY SCI 5", "PHY SCI 105", "PHYSICS 193"]}, {"name": "(b) Select four courses from the following:", "child": ["BIO SCI 1A", "BIO SCI 93", "BIO SCI 94", "CHEM 1A", "CHEM 1B", "CHEM 1C", "EARTHSS 1", "EARTHSS 7", "PHYSICS 20A", "PHYSICS 20B"]}]}, {"name": "Specialization in Astrophysics", "child": [{"name": "The Astrophysics specialization is primarily taken by two types of students, those planning on going on to graduate school in astronomy or astrophysics and those planning to work in aeronautics or astrophysics-related industries or government research laboratories after receiving their bachelor’s degree. It also is an excellent focus for students who anticipate careers in science journalism, teaching, science administration, or public relations. The course work includes:", "child": []}, {"name": "(b) A. Complete:", "child": ["PHYSICS 61C", "PHYSICS 139"]}, {"name": "(b) B. Select three courses from the following:", "child": ["PHYSICS 137", "PHYSICS 138", "PHYSICS 144", "PHYSICS 145"]}, {"name": "(b) C. Two or more upper-division Physics courses. Of the Physics electives, students bound for graduate school are strongly advised to include:", "child": ["PHYSICS 113B", "PHYSICS 125B"]}, {"name": "Other recommended electives include:", "child": ["PHYSICS 116", "PHYSICS 134A", "PHYSICS 135", "PHYSICS 136"]}]}]', 'EDUC 55, EARTHSS 1, LPS 30, BIO SCI 93, I&C SCI 31, CHEM 1A, PHYSICS 52C, PHYSICS 111A, PHYSICS 111B, LPS 141D, LPS 60, PHYSICS 144, MATH 107L, CHEM 1B, PHYSICS 125B, LPS H80, MATH 105A, LPS 140, PHY SCI 105, PHYSICS 7D, MATH 2B, PHYSICS 61C, LPS 105B, BIO SCI 1A, CHEM 1C, PHYSICS 52B, PHYSICS 61A, PHYSICS 113B, PHYSICS 112A, BIO SCI 94, PHYSICS 112B, SOC SCI H1G, MATH 3A, PHYSICS 20B, PHYSICS 50, PHYSICS 7LC, PHYSICS 135, PHYSICS 7C, PHYSICS 52A, I&C SCI 33, MATH 3D, PHYSICS 53, PHY SCI 5, MATH 105B, MATH 105LA, PHYSICS 137, MATH 150, LPS 105A, MATH 107, PHYSICS 116, PHYSICS 121W, PHYSICS 136, LPS H125, LPS 40, PHYSICS 113A, PHYSIC