INSERT INTO programs (name, isMajor, requirement, required_courses, url) VALUES ('Psychology, B.A.', '1', '[{"name": "A. Complete the following:", "child": [{"name": "", "child": ["PSYCH 9A", "PSYCH 9B", "PSYCH 9C"]}]}, {"name": "B. Two introductory courses (eight units) in the social sciences selected from:", "child": [{"name": "", "child": ["ANTHRO 2A", "ANTHRO 2B", "ANTHRO 2D", "ECON 1", "LSCI 3", "POL SCI 11C", "SOC SCI 5A", "SOCIOL 1", "SOCIOL 2", "SOCIOL 3"]}, {"name": "Or one or two quarters of the following when topic is not psychology:", "child": ["SOC SCI H1E", "SOC SCI H1F", "SOC SCI H1G"]}]}, {"name": "C. A one-quarter course and laboratory in experimental psychology or research methods selected from the following:", "child": [{"name": "", "child": ["PSYCH 112A", "PSYCH 112LA", "PSYCH 112M", "PSYCH 112LM", "PSYCH 112R", "PSYCH 112LR"]}, {"name": "NOTE: These courses have as prerequisites PSYCH 9A, PSYCH 9B, PSYCH 9C and one year of mathematics/statistics (see course listings).", "child": []}, {"name": "These prerequisites are strictly enforced. PSYCH 112A, PSYCH 112LA are the first quarter of a multi-quarter sequence that satisfies the upper-division writing requirement and allows students to plan and conduct research projects.", "child": []}, {"name": "Students taking these courses should plan to continue in them through at least the second quarter. Students who intend to fulfill the upper-division writing requirement in some other way should consider taking PSYCH 112D and PSYCH 112LD, PSYCH 112M and PSYCH 112LM, or PSYCH 112R and PSYCH 112LR to fulfill the laboratory requirement.", "child": []}]}, {"name": "D. Select four upper-division Psychology core courses (16 units):", "child": [{"name": "These courses are designated with an ending number 0 and include the following:", "child": []}, {"name": "", "child": ["PSYCH 120A", "PSYCH 120D", "PSYCH 120H", "PSYCH 120P", "PSYCH 130A", "PSYCH 140C", "PSYCH 140L", "PSYCH 140M", "PSYCH 150", "PSYCH 160A", "PSYC